import React, { useState, useEffect } from 'react';
import Calculator from '../components/calculator';

import './mainPage.css'

function MainPage() {
  const [salary, setSalary] = useState<number>(0);
  const [year, setYear] = useState<number>(2012);
  const [formStatus, setFormStatus] = useState<boolean>(false)

  const currentYear = new Date().getFullYear();
  let buttonClasses = '';

  const handleInputChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const value = Number(event.target.value);


    event.target.id === 'year' ? setYear(value) : 
    event.target.id === 'salary' ? setSalary(value) : 
    console.log('Dev comment: Undefined input!');

    year < 2012 || year > currentYear ? setFormStatus(false) : 
    salary < 0 ? setFormStatus(false) : 
    setFormStatus(true);
  };

  const formSubmitHandler = (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();
  }


  useEffect(() => {
    buttonClasses = year < 2012 || year > currentYear ? "deactive" : 
    salary < 0 ? "deactive" : "active";
  },[salary, year])



  return (
    <div className="mainPage">
      <h2>TAX Calculator</h2>
      <form onSubmit={formSubmitHandler}>
        <label htmlFor="salary">Enter Salary:
        <input
          type="number"
          id="salary"
          value={salary}
          onChange={handleInputChange}
        /></label>
        <label htmlFor="year">Enter Year:
        <input
          type="number"
          id="year"
          value={year}
          onChange={handleInputChange}
        /></label>
        <button className={bottonClasses} type="submit">CALCULATE NOW</button>
      </form>
      {formStatus ? <Calculator salary={salary} year={year} /> : <p style={{marginTop: 50}}>Waiting for proper data to calculate...</p>}
    </div>
  );
}

export default MainPage;
