import { useEffect, useState } from 'react';
import { Formatter } from '../utility/formatter';

interface CalculatorProps {
salary: number;
year: number;
}

interface TaxBracket {
min: number;
max?: number;
rate: number;
}

interface TaxData {
tax_brackets: TaxBracket[];
}


const Calculator = ({ salary, year }: CalculatorProps) => {
const [result, setResult] = useState<TaxData | null>(null);
const [errorMsg, setErrorMsg] = useState<string | null>()

useEffect(() => {
  const fetchTaxData = async () => {
    await fetch(`http://localhost:5000/tax-calculator/tax-year/${year}`)
      .then (response => {
        if (response && !response.ok) {
          setErrorMsg('Something wrong happened, please try again later.')
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then((data) => {
        setErrorMsg(null);
        console.log("Received data successfully.")
        setResult(data);
      })
      .catch( async (error)  => {
        await setErrorMsg("Something went wrong with data, please try again later.")
        console.error('Error fetching tax data:', error); 
      });
  }

  fetchTaxData();
},[]);



// get the bracket - calculate the tax - go grab the next bracket.
const calculateTaxAmount = (salary: number, tax_brackets: TaxBracket[]): number => {
  let remainingSalary = salary;
  let totalTax: number = 0;

  for (const bracket of tax_brackets) {
    const { min, max, rate } = bracket;
    let bucketTax = 0

    if(remainingSalary > 0){

      bucketTax = typeof max ==="number" ? (Math.min(max, remainingSalary) - min)*rate : (remainingSalary - min)*rate;

      remainingSalary = typeof max ==="number" && max < remainingSalary ? remainingSalary-(max - min) :
      typeof max ==="number" && max > remainingSalary ? Math.max(remainingSalary - min, 0) : 
      typeof max ==="undefined" ? remainingSalary - min : 0;

      console.log({"min": min, "max": max, "rate": rate, "bucketTax": bucketTax, "remainingSalary": remainingSalary});

    } else { 
      bucketTax = 0
      console.log({"min": min, "max": max, "rate": rate, "bucketTax": bucketTax, "remainingSalary": remainingSalary})
    }


  }

  console.log("Tax calculated.")
  return totalTax;
};





return (
  <div className="calculator">
    <h3>Calculation Result</h3>
    {result ? (
      <>
        <p>Salary: {Formatter(salary)}</p>
        <p>Tax Amount: <span data-testid="contentinfo">{Formatter((calculateTaxAmount(salary, result.tax_brackets)))}</span></p>
      </>
      ) : ( !errorMsg ? (
        <>
          <p>Loading tax data...</p>
        </> 
        ) : (
        <>
          <p>{errorMsg}</p>
        </>
      )
    )}
  </div>
);
}

export default Calculator;
